#include <stdio.h>

int main(void)
{
	int num = 15; // 00000000 00000000 00000000 00001111
	int result1 = num << 1; //num의 비트 열을 왼쪽으로 1칸씩 이동
	int result2 = num << 2; 
	int result3 = num << 3;

	printf("1칸 이동 결과: %d \n", result1); //30
	printf("2칸 이동 결과: %d \n", result2); //60
	printf("3칸 이동 결과: %d \n", result3); //120
	return 0;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     